import './ExpenseForm.css'
import { useState } from 'react';

const ExpenseForm = () => {
    const [changedTitle, changeTitle] = useState('');
    const [changedAmount, changeAmount] = useState('');
    const [changedDate, changeDate] = useState('');
    // const [userInput, changeUserInput] = useState({
    //     enteredTitle : '',
    //     enteredAmount : '',
    //     enteredDate : ''
    // });

    const titleChangeHandler = (event) => {
        // changeUserInput({
            //     ...userInput,
            //     enteredTitle : event.target.value
            // });

            changeTitle(event.target.value);
    }

    const amountChangeHandler = (event) => {
        // changeUserInput({
        //     ...userInput,
        //     enteredAmount : event.target.value
        // });


    }

    const dateChangeHandler = (event) => {
        // changeUserInput({
        //     ...userInput,
        //     enteredDate : event.target.value
        // });

        
    }

    return (
        <form>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type="text" onChange={titleChangeHandler} />
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input type="number" min="0.04" step="0.01" onChange={amountChangeHandler} />
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type="date" min="2019-01-01" max="2022-02-02" onChange={dateChangeHandler} />
                </div>
                <div className="new-expense__actions">
                    <button type="submit">Add Expense</button>
                </div>
            </div>
        </form>
    );
}

export default ExpenseForm;
