import ExpenseItem from './ExpenseItem';
import useState from 'react';
import ExpensesFilter from './ExpensesFilter';
import Card from '../UI/Card';
import './Expenses.css';

const Expenses = props => {
    const [filteredYear, setFilteredYear] = useState('2020');
 
    const filterChangeHandler = (selectedYear) => {
        setFilteredYear(selectedYear);
    }

    return (
        <Card className="expenses">
            <ExpensesFilter selected = {filteredYear} onChangeFilter = {filterChangeHandler}></ExpensesFilter>

            <ExpenseItem date={props.expenses[0].date} amount={props.expenses[0].amount} title={props.expenses[0].title}></ExpenseItem>

            <ExpenseItem date={props.expenses[1].date} amount={props.expenses[1].amount} title={props.expenses[1].title}></ExpenseItem>

            <ExpenseItem date={props.expenses[2].date} amount={props.expenses[2].amount} title={props.expenses[2].title}></ExpenseItem>
            
            <ExpenseItem date={props.expenses[3].date} amount={props.expenses[3].amount} title={props.expenses[3].title}></ExpenseItem>
        </Card>
    );
}

export default Expenses;
