// {
//     id: 'e1',
//     title: 'Toilet Paper',
//     amount: 94.12,
//     date: new Date(2020, 7, 14),
//   }
import Chart from '../Chart/Chart';
import { useState } from 'react';

const ExpensesChart = props => {
    const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
    const [maxValue, setMaxValue] = useState(0);
    const monthExpenses = [];
    for (let i = 0; i < 12; i++) monthExpenses.push({ value: 0, label: months[i] });
    props.expenses.forEach((expense) => {
        let monthNum = expense.date.getMonth();
        monthExpenses[monthNum].value = monthExpenses[monthNum].value + parseInt(expense.amount);
        if (monthExpenses[monthNum].value > maxValue) setMaxValue(monthExpenses[monthNum].value);
    })
    return (<Chart datapoints={monthExpenses} maxValue={maxValue}></Chart>);
}

export default ExpensesChart;
